import React, { useEffect, useState } from "react";
import axios from "axios";
import ProductCard from "./ProductCard";
import '../App.css';

export default function Products() {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    axios.get("http://localhost:3001/products")
      .then(res => setProducts(res.data))
      .catch(() => setProducts([]));
  }, []);

  return (
    <div className="container my-5 products-section">
      <h2 className="mb-4">Öne Çıkan Ürünler</h2>
      <div className="row">
        {products.map((product) => (
          <div className="col-md-4 mb-4 product-card" key={product.id}>
            <ProductCard id={product.id} name={product.name} price={product.price} img={product.imageUrl || product.img} />
          </div>
        ))}
      </div>
    </div>
  );
}
